            </div>
          </div>


          {/* <p className="mt-3">You will receive your code shortly</p>
          <p className="fs-5">Your payment receipt is what you will use to get your exam form</p> */}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default OTPValidation;
